import ToastMixin from "@/mixins/toastMixin.js";
export default {
  name: "formAddTask",
  mixins: [ToastMixin],

  data() {
    return {
      formAddTask: {
        subject: "",
        description: ""
      },
      methodSave: "new"
    };
  },

  created() {
    if (this.$route.params.index === 0 || this.$route.params.index !== undefined) {
      this.methodSave = "update";
      let tasks = JSON.parse(localStorage.getItem("tasks"));
      this.formAddTask = tasks[this.$route.params.index];
    }
  },

  methods: {
    saveTask() {
      if (this.methodSave === "update") {
        let tasks = JSON.parse(localStorage.getItem("tasks"));
        tasks[this.$route.params.index] = this.formAddTask;
        localStorage.setItem("tasks", JSON.stringify(tasks));
        this.showToast("success", "Success!", "Task updated successfully");
        this.$router.push({
          name: "list"
        });
        return;
      }

      let tasks = localStorage.getItem("tasks") ? JSON.parse(localStorage.getItem("tasks")) : [];
      tasks.push(this.formAddTask);
      localStorage.setItem("tasks", JSON.stringify(tasks));
      this.showToast("success", "Success!", "Task created successfully!");
      this.$router.push({
        name: "list"
      });
    }

  }
};
